'use strict';

var isColorHue = require('./utils/is-color-hue.cjs');
var isObject = require('./utils/is-object.cjs');

function extractColorSchemeTypes(theme) {
  const { colors } = theme;
  if (!isObject.isObject(colors)) {
    return [];
  }
  return Object.entries(colors).reduce(
    (acc, [colorName, colorValues]) => {
      if (isColorHue.isColorHue(colorValues)) {
        acc.push(colorName);
      }
      return acc;
    },
    []
  );
}

exports.extractColorSchemeTypes = extractColorSchemeTypes;
